<?phpnamespace Admin\Controller;use Think\Controller;class LoginController extends Controller{		//默认方法,判断where字符串决定跳转位置	public function index()	{		$this->display('Login');	}		//登录判断	public function enter()	{		$m = M('admin');		$res = $m->where("username='{$_POST['username']}'")->find();		if($res)		{			if($res['password'] == $_POST['password'])			{				//var_dump($res);die;				switch($res['status'])				{					case '0':						$this->error('账号已被禁用,请联系管理员');					break;										case '3':						if($res['finish_time'] <= time())							$this->error('账号已过期,请联系管理员');					break;				}				session('adminname',$res['username']);				session('status',$res['status']);				$this->success('登录成功', '/Admin/AdminPage/index',1);			}else				$this->error('账号或密码错误,请重新输入');		}else{			$this->error('账号或密码错误,请重新输入');		}	}		}?>