$(function() {
    
    var sources = [];
    for (var i = 1; i < 20; i += 1) {
        var num = i;
        if (i < 10) { num = '0' + i }
        var url = "./images/t_nw" + num + ".jpg";
        sources.push(url)
    };
    sources.sort(function() {
        return Math.random() > 0.5 ? -1 : 1
    });
    var nSlide = new NSlide("#nslide", { width: window.innerWidth * 0.22, height: window.innerHeight, sources: sources });
    $(".ww-aside-navbar").on("mouseover", function() { nSlide.flip() });
    $(document).on("mouseover", ".ww-aside-bottom a", function() {
        var top = $(this).offset().top + 3 - $(window).scrollTop();
        var title = $(this).attr("data-title");
        $(".ww-grumble").css({ "top": top + "px" }).text(title).show()
    }).on("mouseout", function() { $(".ww-grumble").hide() });
   
});
